    public void BCCalculate(){
        double weight = Double.parseDouble(Weight.getText()); // Weight in kg
        double height = Double.parseDouble(Height.getText()) / 100; // cm to meters
        int age = Integer.parseInt(Age.getText()); // Age
        String gender = Male.isSelected() ? "Male" : "Female";
        String activityLevel = (String) CBActivity.getSelectedItem();

        // Calculate BMI
        double bmi = weight / (height * height);
        BMI.setText(String.format("%.2f", bmi));

        double bmr;
        if (gender.equals("Male")) {
            bmr = 66 + (6.23 * weight) + (12.7 * height * 100) - (6.8 * age); // boi
        } else {
            bmr = 655 + (4.35 * weight) + (4.7 * height * 100) - (4.7 * age); // girl
        }

        double calorieIntake;
        switch (activityLevel) {
            case "Sedentary: little to no exercise/week":
                calorieIntake = bmr * 1.2;
                break;
            case "Light: exercises 1-3 times/week":
                calorieIntake = bmr * 1.375;
                break;
            case "Moderate: exercises 4-5 times/week":
                calorieIntake = bmr * 1.55;
                break;
            case "Active: daily exercise/intense exercise 3-5 times/week":
                calorieIntake = bmr * 1.725;
                break;
            case "Very Active: intense exercise 6-7 times/week":
                calorieIntake = bmr * 1.9;
                break;
            default:
                calorieIntake = bmr; 
                break;
        }

        RCI.setText(String.format("%.0f", calorieIntake)); // Cal intake
        
        if (bmi < 18.5) {
            Status.setText("Underweight");
        } else if (bmi < 24.9) {
            Status.setText("Normal weight");
        } else if (bmi < 29.9) {
            Status.setText("Overweight");
        } else {
            Status.setText("Obese");
        }
        
        //calorie deficit, weight L
        
        double caloriesPerKg = 7700; // Calories needed to lose 1 kg
        double mildWeightLoss = 0.25; // kg/week
        double regularWeightLoss = 0.5; // kg/week

        // Calculate daily deficit for mild weight loss
        double mildDailyDeficit = (mildWeightLoss * caloriesPerKg) / 7;
        MildWL.setText(String.format("%.0f", calorieIntake - mildDailyDeficit)); 

        // Calculate daily deficit for regular weight loss
        double regularDailyDeficit = (regularWeightLoss * caloriesPerKg) / 7;
        WL.setText(String.format("%.0f", calorieIntake - regularDailyDeficit)); 
    }
    
